<script>import Popper from '../utils/Popper.svelte';
import classNames from 'classnames';
export let type = 'dark';
export let defaultClass = 'py-2 px-3 text-sm font-medium';
const types = {
    dark: 'bg-gray-900 text-white dark:bg-gray-700',
    light: 'border border-gray-200 bg-white text-gray-900',
    auto: ' bg-white text-gray-900 dark:bg-gray-700 dark:text-white border border-gray-200 dark:border-0',
    custom: ''
};
let toolTipClass;
$: {
    if ($$restProps.color)
        type = 'custom';
    else
        $$restProps.color = 'none';
    toolTipClass = classNames('tooltip', defaultClass, types[type], $$props.class);
}
</script>

<Popper rounded shadow {...$$restProps} class={toolTipClass} on:show>
  <slot />
</Popper>
